// utils/logInteraction.js
const Interaction = require("../modals/Interaction");

const logInteraction = async ({ user_id, product_id, type, value }) => {
  try {
    await Interaction.create({
      user_id,
      product_id,
      interaction_type: type,
      interaction_value: value,
    });
  } catch (error) {
    console.error("Gagal mencatat interaksi:", error.message);
  }
};

module.exports = logInteraction;
