// models/Interaction.js
const mongoose = require("mongoose");

const interactionSchema = new mongoose.Schema({
  user_id: {
    type: String,
    required: true,
  },
  product_id: {
    type: String,
    required: true,
  },
  interaction_type: {
    type: String,
    enum: ["click", "add_to_cart", "purchase"],
    required: true,
  },
  interaction_value: {
    type: Number,
    default: 1,  // Masih bisa digunakan untuk bobot, jika mau
  },
  timestamp: {
    type: Date,
    default: Date.now,
  },
});

module.exports = mongoose.model("Interaction", interactionSchema);
