const express = require("express");
const router = express.Router();
const multer = require("multer");
const path = require("path");
const interactionController = require("../controllers/interactionController");

// Setup multer dengan diskStorage
const storage = multer.diskStorage({
  destination: function (req, file, cb) {
    cb(null, path.join(__dirname, "../uploads"));
  },
  filename: function (req, file, cb) {
    cb(null, Date.now() + "-" + file.originalname);
  },
});
const upload = multer({ storage });

// Middleware lokal untuk bypass json parsing
const rawUploadRoute = express.Router();
rawUploadRoute.post(
  "/import",
  upload.single("file"),
  (req, res, next) => {
    if (!req.file) {
      return res.status(400).json({ message: "No file uploaded" });
    }
    next();
  },
  interactionController.importInteractions
);

// Gabungkan rawUploadRoute dan lainnya
router.use(rawUploadRoute);

router.post("/", interactionController.addInteraction);
router.get("/", interactionController.getAllInteractions);
router.get("/recommendation/:userId", interactionController.getRecommendationForUser);

module.exports = router;
